package com.alia.tools.container;import com.alia.tools.client.MockClient;import com.alia.tools.entity.MockServiceInfoDTO;import com.alibaba.dubbo.rpc.service.GenericService;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.stereotype.Controller;import java.io.IOException;/** * Created by Alia on 2016/12/17. */@Controller@EnableAutoConfigurationpublic class MockServer {    private static GenericService genericService;    private static MockServiceInfoDTO serviceInfo;    public MockServer() {    }    public static void run(Class cls, MockServiceInfoDTO si, String... args) throws IllegalAccessException, InstantiationException {        genericService = (GenericService) cls.newInstance();        serviceInfo = si;        main(args);    }    public static void main(String[] args) {        MockClient client = new MockClient(serviceInfo);        try {            client.start(genericService);            SpringApplication.run(MockServer.class, args);            System.in.read();        } catch (IOException var6) {            var6.printStackTrace();        } finally {            client.stop(genericService);        }    }}